element.insertAfter(menu);
}
let songButtons=[];
function addSongButtons(){
    songButtons.forEach((e,i)=>{
    $(e).remove();
    })
    let songs=$("div[data-clip-id]");
    songs.each((i,e)=>{
      let mainDiv=$(e);
        let songId=mainDiv.attr("data-clip-id")
        let stack=mainDiv.find("div").find("div").first();
        let name=mainDiv.find("a[href]").first();
        let filename=name.text();
        if(filename.length<3){
        let findnext=name.parent().next("p");
         filename=findnext.text()
        }
        let dwnlBtn=dwnldBtn("https://cdn1.suno.ai/"+songId+".mp3",filename);
        let added=$(dwnlBtn).insertAfter(stack);
        songButtons.push(added)
        console.log(songId,stack,name.text());
    })
    console.log(songButtons)
}
