$('body').on('click', 'button.mp3download', function() {
    let url=$(this).attr("data-url");
    let name=$(this).attr("data-name");
   download(url,name);
});
$('body').on('click', 'a.reinit', async function() {
    await waitForLoad();
       addSongButtons();
});
async function waitForLoad() {
    return new Promise((resolve, reject) => {
        let maxRetries = 5;

        const checkSpinner = () => {
            const spinner = $(".chakra-spinner");
            if (spinner.length === 0) {
                resolve(); // Chakra spinner bulunmadığında Promise'i çöz
            } else {
                if (maxRetries === 0) {
                    reject(new Error("Chakra spinner hala yüklenmedi.")); // Max yeniden deneme sayısına ulaşıldığında hata fırlat
                } else {
                    maxRetries--;
                    setTimeout(checkSpinner, 1000); // Chakra spinner hala varsa, bir saniye sonra tekrar kontrol et
                }
            }
        };

        checkSpinner(); // İlk kontrolü başlat
    });
}

async function download(dataurl, fileName) {
    const response = await fetch(dataurl);
    const blob = await response.blob();

    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = fileName;
    link.click();
}
